def longest_common_subsequence(str1, str2):
    n = len(str1)
    m = len(str2)

    # Opret en matrice til at opbevare løsningsen.
    lcs = [[0 for i in range(n + 1)] for j in range(m + 1)]

    # Beregn løsningen fra højre mod venstre og nede mod oven.
    for i in range(n, -1, -1):
        for j in range(m, -1, -1):
            if str1[i] == str2[j]:
                lcs[j][i] = 1 + lcs[j + 1][i + 1]
