def binary_search(array, element):
    midpoint = len(array) // 2
    if midpoint == 0:
        return False
    elif array[midpoint] == element:
        return True
    else:
        if midpoint > 0:
            if array[midpoint - 1] < array[midpoint]:
                return binary_search(array[:midpoint], element)
            else:
                return binary_search(array[midpoint:], element)
