def lcs(x, y):
    # Initialize a 2D array to store the LCS lengths
    dp = [[0 for _ in range(len(y)+1)] for _ in range(len(x)+1)]

    # Fill the array with LCS lengths starting from the ends of the strings
    for i in range(len(x), 0, -1):
        for j in range(len(y), 0, -1):
