def fibonacci(n):
    if n == 0 or n == 1:
        return n

    cache = {0: 0, 1: 1}
    def fib_recursive(n):
        if n in cache:
            return cache[n]
        else:
            result = fib_recursive(n - 1) + fib_recursive(n - 2)
