function checkIfPrime(numbers){
let isPrime=true;
numbers.forEach(number => {
    for(let i=2; i<=number/2; i++){
        if(number%i === 0) {
            isPrime = false;
            break;
        }
    }
    if(!isPrime) break;
});
return isPrime;
}

