```python
def longest_common_substring(str1, str2):
    """Returns the longest common substring of two strings."""
    # Initialize dynamic programming table with all 0s
    table = [[0 for _ in range(len(str2) + 1)] for _ in range(len(str1) + 1)]

    # Loop through each character of string 1 and string 2
    for i in range(len(str1) + 1):
        for j in range(len(str2) + 1):
            if i == 0 or j == 0:
                table[i][j] = 0
            elif str1[i - 1] == str2[j - 1]:
                table[i][j] = table[i - 1][j - 1] + 1
            else:
                table[i][j] = max(table[i][j - 1], table[i - 1][j])

    # Find the maximum value in the table to get the length of the substring
    max_value = max(table[-1])

    # Loop through the table to find the starting and ending indices of the substring
    i, j = len(str1), len(str2)
    while max_value > 0:
        if str1[i - 1] == str2[j - 1]:
            max_value -= 1
            i -= 1
            j -= 1
        elif table[i][j - 1] > table[i - 1][j]:
            j -= 1
        else:
            i -= 1

    # Return the substring from the starting and ending indices found in the previous step
    return str1[i:j + 1]

