def longest_common_subsequence(str1, str2):
    # initierer en 2D-matrise med lengden til str1 som radlengde og str2 som kolonne lengde
    result = [[0] * len(str2) for _ in range(len(str1))]

    # gjennomgår alle strengene
    for i in range(len(str1)):
        for j in range(len(str2)):
            # Hvis karakterene i str1 og str2 er like, legger vi til én i matrisen
            if str1[i] == str2[j]:
                result[i][j] = result[i - 1][j - 1] + 1
            # Hvis karakterene i str1 og str2 ikke er like, setter vi den lengste felles delsekvensen til null
            else:
                result[i][j] = max(result[i - 1][j], result[i][j - 1])

    # returnerer den lengste felles delsekvensen mellom str1 og str2
    return result[len(str1) - 1][len(str2) - 1]

