string convertMarkdownToHtml(const string& markdown) {
    // 使用正则表达式处理 Markdown 文本，将其转换为 HTML 格式
    string html = replaceAll(markdown, "\\*", "<b>");
    html = replaceAll(html, "\\_", "<i>");
    html = replaceAll(html, "\\[", "<a href=\"");
    html = replaceAll(html, "\\]", "\">");
    html = replaceAll(html, "\\(", "</a>");
    html = replaceAll(html, "\\#", "</a>");
    html = replaceAll(html, "\\`", "<code>");
    html = replaceAll(html, "\\`", "</code>");
    return html;
}

// 替换字符串中的所有匹配项为指定的替换项的函数
string replaceAll(const string& str, const string& from, const string& to) {
    size_t start_pos = 0;
    while ((start_pos = str.find(from, start_pos)) != string::npos) {
        str.replace(start_pos, from.length(), to);
        start_pos += to.length(); // Handles case where 'to' is a substring of 'from'
    }
    return str;
}

