const { Client, SlashCommandBuilder } = require("discord.js");
const client = new Client();

const banCommand = new SlashCommandBuilder()
    .setName("ban")
    .setDescription("Ban a user from the server")
    .addUserOption(option => 
        option.setName("user")
            .setDescription("The user to ban")
            .setRequired(true));

client.on("interactionCreate", async interaction => {
    if (interaction.isCommand()) {
        if (interaction.commandName === "ban") {
            const user = interaction.options.getUser("user");
            const guild = interaction.guild;
            if (guild) {
                try {
                    await guild.members.ban(user);
                    await interaction.reply("User banned successfully");
                } catch (error) {
                    await interaction.reply(`Error banning user: ${error}`);
                }
            } else {
                await interaction.reply("You must be in a server to use this command");
            }    
        } else {    
            await interaction.reply({ content: "Invalid command" }); 
        }    
    }    
});    

client.on("ready", () => {    
    client.application.commands.create(banCommand);    
});    
```

