```c++
#include <iostream>
#include <fstream>
#include <string>

int main() {
    std::string word; // The word to count the occurrences of
    std::cout << "Enter the word to count: ";
    std::cin >> word; // Get the input from the user

    std::ifstream file("filename.txt"); // Open the file for reading
    std::string line; // A string to hold each line of the file
    int count = 0; // Initialize the count variable to 0
    while (std::getline(file, line)) { // Read each line of the file and store it in the line variable
        int found = 0; // Initialize a counter for each line
        std::string::size_type pos = 0; // Initialize the position of the first occurrence of the word in each line
        while ((pos = line.find(word, pos)) != std::string::npos) { // Loop through each occurrence of the word in each line
            ++found; // Increment the counter for each occurrence of the word in each line
            pos += word.size(); // Move the position to after the current occurrence of the word
        }
        count += found; // Add the number of occurrences of the word in each line to the total count variable
    }
    std::cout << "The word '" << word << "' was found " << count << " times in the file." << std::endl; // Output the result to the console
}
