            elif string1[i - 1] == string2[j - 1]:
                dp[i][j] = dp[i - 1][j - 1] + 1
            else:
                dp[i][j] = max(dp[i - 1][j], dp[i][j - 1])

    # Trace back LCS path and return length
    i, j = m, n
    lcs_length = dp[m][n]
    lcs = []
    while i > 0 and j > 0:
