def lcs(X, Y):
    # m and n are the lengths of the input strings X and Y respectively
    m = len(X)
    n = len(Y)

    # dp is a 2D array to store the solutions of subproblems
    dp = [[0 for _ in range(n + 1)] for _ in range(m + 1)]

    # Fill dp table with base cases
    for i in range(m + 1):
        dp[i][0] = 0

    for j in range(n + 1):
        dp[0][j] = 0

    # Fill dp table with solutions of subproblems using dynamic programming approach
    for i in range(1, m + 1):
        for j in range(1, n + 1):
            if X[i - 1] == Y[j - 1]:
                dp[i][j] = dp[i - 1][j - 1] + 1 # Incrementing length if character matches
