# Longest Common Subsequence (LCS) using Dynamic Programming

def lcs(X: str, Y: str) -> int:
    m = len(X)
    n = len(Y)
    C = [[0] * (n + 1) for _ in range(m + 1)]

    for i in range(m + 1):
        for j in range(n + 1):
            if i == 0 or j == 0:
                C[i][j] = 0
            elif X[i - 1] == Y[j - 1]:
                C[i][j] = C[i - 1][j - 1] + 1
            else:
                C[i][j] = max(C[i - 1][j], C[i][j - 1])

    return C[m][n]

