import java.util.*;

public class PowerOfThreeSolution {

    public static boolean isPowerOfThree(int n) {
        // If n is less than or equal to 0, it is not a power of three.
        if (n <= 0) return false;
        // If n is greater than the largest power of three, it is not a power of three.
        if (n > Integer.MAX_VALUE / 3) return false;
        // Check if n is a power of three using the logarithm method.
        double log3n = Math.log(n) / Math.log(3);
        int floorLog3n = (int) Math.floor(log3n);
        return floorLog3n == log3n;
    }

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        System.out.println(isPowerOfThree(n) ? "true" : "false");
    }
}

