using namespace std;

string convertMarkdownToHtml(const string& markdownString) {
    // Regulære uttrykk for å erstatte markdown-formatering med html-formatering
    regex boldRegex("(\\*\\*[^\\*]\\*\\*)");
    regex italicRegex("(\\*[^\\*]\\*)");
    regex headingRegex("(^#+ )");
    regex linkRegex("(\\[.*?\\]\\((.*?)\\))");

    string htmlString = markdownString; // Kopier innmaten i markdownString til htmlString

    // Erstatte fett med <strong>...</strong>
    htmlString = regex_replace(htmlString, boldRegex, "<strong>$1</strong>");

    // Erstatte kursiv med <em>...</em>
    htmlString = regex_replace(htmlString, italicRegex, "<em>$1</em>");

    // Erstatte overskrifter med h1-h6 etter antall #-tegn
    string replacement;
    smatch match;
    while (regex_search(htmlString, match, headingRegex)) {
        int numHashes = match[0].size() - 1; // Antall #-tegn minus ett (for å unngå h1)
        replacement = "<h" + to_string(numHashes) + ">"; // Opprett hn-tag med riktig nummer av n (n = antall #-tegn)
        htmlString = regex_replace(htmlString, match[0], replacement); // Erstatte overskrift med nytt hn-tag
    }

    // Erstatte lenker med <a href="...">...</a>-tagger 
    string linkText; string linkUrl;  // Holder linker og urler fra markdown-lenker 
    while (regex_search(htmlString, match, linkRegex)) {  // Gjennomgå hver enkelt markdown-lenke i strengen 
        linkText = match[1];                              // Hent teksten fra lenken (inneholder ikke []-parenteser) 
        linkUrl = match[2];                               // Hent urlen fra lenken (inneholder ikke []-parenteser) 
        htmlString = regex_replace(htmlString, match[0], "<a href=\"" + linkUrl + "\">" + linkText + "</a>"); // Erstatte lenken med ny <a href="...">...</a>-tag 
    } 

    return htmlString; // Returner den endelige HTML-strengen 
}

