import java.math.BigInteger;

public class PowerOfThree {

    public static boolean isPowerOfThree(int n) {
        if (n <= 0) {
            return false;
        }
        BigInteger root = new BigInteger("3").pow(new BigInteger("0").add(new BigInteger("1")));
        while (root.compareTo(BigInteger.valueOf(n)) < 0) {
            root = root.multiply(new BigInteger("3"));
        }
        return root.compareTo(BigInteger.valueOf(n)) == 0;
    }
}

