class Queue:
    def __init__(self):
        self.items_to_process = []
        self.items_being_processed = []

    def enqueue(self, item):
        self.items_to_process.append(item)

    def dequeue(self):
        if len(self.items_being_processed) == 0 and len(self.items_to_process) > 0:
            item = self.items_to_process.pop()
            self.items_being_processed.append(item)
            return item
        elif len(self.items_being_processed) > 0:
            item = self.items_being_processed.pop()
            return item
        else:
            return None
