std::string convertMarkdownToHtml(const std::string& input) {
    // Regular expression for converting Markdown syntax to HTML syntax
    std::regex re("\\*([^*]+)\\*");
    std::stringstream result;
    for (const auto& c : input) {
        if (c == '*') {
            result << "<b>";
            result << std::regex_replace(input.substr(0, input.find(' ', 1)), re, "$1");
