def lcs(string1, string2):
    m = len(string1) + 1
    n = len(string2) + 1
    dp = [[0 for _ in range(n)] for _ in range(m)]
    longest_length = 0
    for i in range(m):
        for j in range(n):
            if string1[i] == string2[j]:
                dp[i][j] = dp[i-1][j-1] + 1
                if dp[i][j] > longest_length:
                    longest_length = dp[i][j]
