from collections import deque

class Queue:
    def __init__(self):
        self.stack1 = deque()
        self.stack2 = deque()

    def enqueue(self, value):
        self.stack1.append(value)

    def dequeue(self):
        if not self.stack2:
            while self.stack1:
                self.stack2.append(self.stack1.popleft())
        return self.stack2.pop()

