def lcs(s1, s2):
    # Definieren Sie ein 2D-Array, um die LCS-Tabellen bereitzustellen.
    m = len(s1)
    n = len(s2)
    table = [[0 for i in range(n + 1)] for j in range(m + 1)]

    # Füllen Sie das Array mit Nullen.
    for i in range(m + 1):
        table[i][0] = 0
    for j in range(n + 1):
        table[0][j] = 0

    # Durchlaufen Sie alle Zeichen des ersten Strings und aktualisieren Sie das Array.
    for i in range(1, m + 1):
        for j in range(1, n + 1):
            if s1[i - 1] == s2[j - 1]:
                table[i][j] = table[i - 1][j - 1] + 1
            else:
                table[i][j] = max(table[i - 1][j], table[i][j - 1])

    # Geben Sie die LCS zurück.
