    std::string result = markdown_text;
    std::replace(result.begin(), result.end(), "\n\n", "<p>\n");
    std::replace(result.begin(), result.end(), "\n", "</p>\n<p>\n");

    // Replace headers with HTML tags
    std::replace(result.begin(), result.end(), "# ", "<h1>");
    std::replace(result.begin(), result.end(), "## ", "<h2>");
    std::replace(result.begin(), result.end(), "### ", "<h3>");
    std::replace(result.begin(), result.end(), "#### ", "<h4>");
    std::replace(result.begin(), result.end(), "##### ", "<h5>");
    std::replace(result.begin(), result.end(), "###### ", "<h6>");

    // Replace italicized text with HTML tags
    std::replace(result.begin(), result.end(), "**", "<strong>");
    std::replace(result.begin(), result.end(), "**", "</strong>");

    // Replace bold text with HTML tags
    std::replace(result.begin(), result.end(), "__", "<strong>");
    std::replace(result.begin(), result.end(), "__", "</strong>");

    // Replace links with HTML tags
    std::string link_pattern = "[\\w\\d\\-_]+\\(([\\w\\d\\-_]+)\\)";
    std::regex link_regex = std::regex(link_pattern, std::regex_constants::icase); 

    std::smatch matches; 
