        const auto &match = matches[0]; 
        const auto &link = matches[1]; 
        const auto &url = matches[2]; 
        const auto link_html = "<a href=\"" + url + "\">" + link + "</a>"; 
        result = std::regex_replace(result, link_regex, link_html); 
    } 

    return result; 
} 

