def longest_common_subsequence(string1, string2):
    # Initialize the dynamic programming matrix with -1 for all cells
    lcs_matrix = [[-1 for _ in range(len(string2) + 1)] for _ in range(len(string1) + 1)]

    # Set the diagonal cells to 0 as they represent empty subsequences
