def find_longest_common_subsequence(text1, text2):
    if len(text1) == 0 or len(text2) == 0:
        return 0

    longest_common_subsequence_table = {}
    for i in range(len(text1)):
        longest_common_subsequence_table[i] = {}
        for j in range(len(text2)):
            if text1[i] == text2[j]:
                if i == 0 or j == 0:
                    longest_common_subsequence_table[i][j] = 1
                else:
                    longest_common_subsequence_table[i][j] = longest_common_subsequence_table[i - 1][j - 1] + 1
            else:
                longest_common_subsequence_table[i][j] = max(longest_common_subsequence_table[i - 1][j], longest_common_subsequence_table[i][j - 1])

    return longest_common_subsequence_table[len(text1) - 1][len(text2) - 1]

