def lcs(s1, s2):
    n, m = len(s1), len(s2)
    # Oppretter en matrise med n+1 rader og m+1 kolonner for å lagre LCS-verdiene
    dp = [[0 for _ in range(m+1)] for _ in range(n+1)]
    for i in range(n+1):
        dp[i][0] = 0
    for j in range(m+1):
        dp[0][j] = 0

    for i in range(1, n+1):
        for j in range(1, m+1):
            if s1[i-1] == s2[j-1]:
                dp[i][j] = dp[i-1][j-1] + 1 # Lagrer LCS-verdi hvis karakterer er like
            else:
                dp[i][j] = max(dp[i][j-1], dp[i-1][j]) # Lagrer største LCS-verdi mellom rader og kolonner uten like karakterer

