def fibonacci_dp(n):
    if n <= 1:
        return n
    else:
        fib_table = [[0 for _ in range(n + 1)] for _ in range(n + 1)]
        fib_table[0][0] = 0
        fib_table[0][1] = 1
        for i in range(2, n + 1):
            fib_table[i][0] = 0
            for j in range(1, i + 1):
