def longest_common_subsequence(a: str, b: str) -> int:
    m = len(a)
    n = len(b)
    dp = [[0 for _ in range(n + 1)] for _ in range(m + 1)]
    for i in range(m + 1):
        for j in range(n + 1):
            if a[i - 1] == b[j - 1]:
