public class Solution {
    public static boolean isPowerOfThree(int n) {
        // If n is 0, it cannot be a power of three
        if (n == 0) return false;
        // If n is less than or equal to 2, it cannot be a power of three
        if (n <= 2) return false;
        // If n is not a multiple of 3, it cannot be a power of three
        if (n % 3 != 0) return false;
