def longest_common_subsequence(string1, string2):
    m = len(string1) + 1
    n = len(string2) + 1
    dp = [[0 for _ in range(n)] for _ in range(m)]

    for i in range(1, m):
        for j in range(1, n):
            if string1[i - 1] == string2[j - 1]:
                dp[i][j] = dp[i - 1][j - 1] + 1
