def longest_common_substring(s1, s2):
    if len(s1) < len(s2):
        s1, s2 = s2, s1

    m = [[0] * (len(s2) + 1) for _ in range(len(s1) + 1)]
    longest, x_longest = 0, 0

    for x in range(1, len(s1) + 1):
        for y in range(1, len(s2) + 1):
            if s1[x - 1] == s2[y - 1]:
                m[x][y] = m[x - 1][y - 1] + 1
                if m[x][y] > longest:
                    longest = m[x][y]
                    x_longest = x
            else:
                m[x][y] = 0

    return s1[x_longest - longest: x_longest]

