Her er implementasjonen av Timsort-algoritmen på Lean 4, sammen med kommentarer som forklar koden:
```
import data.array
import data.list.basic

def timsort (a : Array α) : Array α :=
  let n := a.size in
  if n <= 1 then
    a
  else if n <= 64 then
    insertion_sort a
  else
    let lo, hi := a.partition_at (n / 2) in
    let lo_sorted, hi_sorted := timsort lo, timsort hi in
    merge lo_sorted hi_sorted a using (<)
where
  /- Algoritmen for å sortere et delvis sortert array med en miniatyr. -/
  def insertion_sort (a : Array α) : Array α := do {
    for i in [1:a.size] do {
      let j := i - 1 in
      while j >= 0 && a[i] < a[j] do { a[j+1] ← a[j]; j := j - 1 } in {a[j+1] ← a[i]}
    }; a}

  /- Algoritmen for å slå sammen to sorterte miniatyrer. -/
  def merge (lo, hi : Array α) (a : Array α) (using cmp : α → α → Bool) : Array α := do {
    let m := lo.size + hi.size in
    for i in [0:m-1] do {a[i] ← if i < lo.size then lo[i] else hi[i - lo.size]} in a}

  /- Algoritmen for å dele et array ved å finne det minste elementet i hver seksjon av størrelse `pivot`. -/
  def partition_at (a : Array α) (pivot : Nat) : (Array α × Array α) := do {
    let pivot_val := a[pivot] in let mut lo, hi := [0], [pivot+1] in {for i in [0:pivot-1] do {lo := lo :: [a[i]]; if cmp.trans (cmp a[i] pivot_val) then hi := hi :: [a[i]]}}; {lo.reverse, hi.reverse} }

