public boolean isPowerOfThree(int n) {
    // Om n < 1 är det inte en potens av tre
    if (n < 1) {
        return false;
    }

    // Om n = 1 är det en potens av tre (3^0)
    if (n == 1) {
        return true;
    }

    // Räkna ut roten ur 3 till n
