def longest_common_subsequence(str1, str2):
    n = len(str1)
    m = len(str2)

    # Dynamisk programmeringsmatris för att lagra längsta gemensamma delsekvensen
    dp = [[0 for _ in range(m + 1)] for _ in range(n + 1)]

    for i in range(1, n + 1):
        for j in range(1, m + 1):
