```lean4
impl TimSortAlgorithm {
    fn timsort<'a, T>(a: &'a mut [T]) {
        let mut lo = 0;
        let mut hi = a.len() - 1;
        while lo <= hi {
            let (lo, hi) = match (lo, hi) {
                (lo, hi) if lo >= hi => (hi, lo), // reverse order
                (lo, hi) if a[hi] < a[lo] => (hi, lo), // swap low and high elements
                (lo, hi) => (lo, hi), // keep looping for the same order as before
            };
            a[lo] = a[hi];
            a[hi] = a[lo]; // swap low and high elements
            lo += 1; // move to next element to compare next time
            hi -= 1; // move to next element to compare next time
        }
    }
}

