  // translate the markdown text into HTML.
  std::string html;
  for (const auto& c : in) {
    if (c == '*') {
      html += '<strong>';
    } else if (c == '**') {
      html += '<strong style="font-weight: bold;">';
    } else if (c == '_') {
      html += '<em>';
    } else if (c == '~') {
      html += '<del>';
    } else if (c == '/') {
      html += '<hr />';
    } else if (c == '[' && in[in.size() - 1] == ']') {
      // remove the trailing ']' from the string.
      in = in.substr(0, in.size() - 1);
      // remove all the contents of the brackets and add a space character before it.
      html += '<span class="tag">' + in + '</span>';
    } else if (is_valid_link(c)) {
      // convert the link into an HTML anchor tag with the link's destination url.
      std::string destination = convert_to_html_link(in);
      html += '<a href="' + destination + '" target="_blank">' + c + '</a>';
    } else if (is_valid_image(in)) {
      // convert the image into an HTML image tag with its destination url.
      std::string destination = convert_to_html_image(in);
      html += '<img src="' + destination + '" />';
    } else {
      // simply add the character to the string as is.
      html += c;
    }  // end of for loop.
  }  // end of for loop.
