def timsort[A : Type](xs : list[A]) : list[A] :=
  let pivot = xs.head
  def helper(xs : list[A], low : nat, high : nat) : list[A] :=
    if low < high then {
      if pivot < xs.head then {
        helper(xs, low + 1, high)
      } else {
        helper(xs, low, high - 1)
      }
    } else xs // empty list when `low == high`

  def partition(xs : list[A], pivot : A) : (list[A], A) :=
    let mid = (low := low + 1) % (high - low + 1) // index of element smaller than pivot in xs.mid(low, high)
