def longest_common_subsequence(s1, s2):
    """
    Recorre cada carácter de las cadenas para buscar la subsecuencia más larga.
    """
    n1, n2 = len(s1), len(s2)
    if n1 == 0 or n2 == 0:
        return 0, 0
    tabla = [[0 for _ in range(n2)] for _ in range(n1)]
    for i in range(n1):
        for j in range(n2):
            if s1[i] == s2[j]:
