```cpp
#include <string>
#include <sstream>
#include <iostream>
#include <ctime>

std::string formatMarkdownToHtml(std::string input) {
    // Create a stream for the input string
    std::istringstream stream(input);
    // Create a stream for the output string
    std::ostringstream output;
    // Read the stream of characters from the input string into the stream of characters for the output string
    while (stream >> std::ws) {
        // For each character in the stream, check if it is a link and add the appropriate HTML tags around it
        if (stream.peek() == '<') {
            if (stream.peek() == '!') { // is it an inline link? 
                output << "&lt;" << stream.get() << "&gt;"; 
            } else { 
                output << "&lt;a href=\"" << stream.get() << "\"&gt;" << stream.get() << "&lt;/a&gt;"; 
            } 
        } else { // not a link, so just output it as is 
            output << stream.get(); 
        } 
    } 
    return output.str(); 
} 
```

