std::string format_markdown_to_html(const std::string& markdown_text) {
    // Replace all backticks with links.
    markdown_text = markdown_text.replace("`", "[`");
    // Replace all asterisks with links.
    markdown_text = markdown_text.replace("*", "[*");
    // Replace all hashtags with links.
    markdown_text = markdown_text.replace("#", "[#");
    // Replace all emojis with links.
    markdown_text = markdown_text.replace(":)", ":)" );
    // Replace all underlined words with links.
    markdown_text = markdown_text.replace("`", "[`");
    // Convert other special characters to their corresponding HTML entities.
    for (const auto& character : { "\"", "<", ">" }) {
        markdown_text = markdown_text.replace(character, character + "&" + character);
    }
    // Convert the newlines in the text to breaks in the HTML output.
    auto newline_positions = [](const std::string& text) { return std::count(text.begin(), text.end(), '\n'); };
    auto replacements = [](const std::string& text, const int position) { return std::string(1, '\n') + std::string(position, '\n'); };
    markdown_text = markdown_text.replace(newline_positions(markdown_text), replacements(markdown_text, newline_positions(markdown_text)));
