    html.erase(html.begin(), std::find_if(html.begin(), html.end(), [](auto ch) { return std::isspace(ch); }));
    html.replace(html.begin(), html.end(), " ", "&nbsp;");
    // Replace all tabs with '&amp;#9'
    html.replace(html.begin(), html.end(), "\t", "&amp;#9");
    // Convert newline to &lt;br /&gt;
    html.replace(html.begin(), html.end(), "\n", "<br />");
    // Converts links into HTML anchor links
    auto links = std::regex_match(html, std::regex("\\[(.*?)]\\s?"));
    if (links) {
        for (const auto &match : links) {
            auto link = match[1]; // The link text itself
