    while(getline(file, line)) { //Lest linjenett i tekstfilen linje for linje og sørger for at filen ikke blir lest helt ut til slutt. 
        std::map<std::string, int> wordcount; //Skapte et ord/antall-mangfoldig hash-tall og legger ordet i denne variablen. 
        for (auto& ch: line) { //Gjennomstrekker hver eneste tegn i linjen  og legger det inn i variablen som vil bli brukt til å hente antall forekomster av hvert enkelt ord. 
            if (ch == ' ' || ch == '\t' || ch == '\n') continue; //Slipp over tegn som ikke er ord, som er ikke inkludert i ordlisten og som ikke kan brukes for å finne ut antall forekomster. 
            else if (!wordcount.count(ch)) wordcount[ch] = 1; //Henter antall forekomster av ordet hvis det ikke allerede eksisterer i ordlisten. 
            else wordcount[ch]++; //Henter antall forekomster av ordet hvis det allerede eksisterer i ordlisten. 
        } 

        cout << "The word count is: " << wordcount["word"] << endl; //Synger ut hvor mange ganger ordet "word" opptrer i tekstfilen. 
    }  

    file.close(); //Lukker filen. 
}

