function dp[N][N](dp: Array[Array[Int]], state: Array[Int], row: Int, col: Int): Int = {
  // 如果已经求出了解答
  if (dp.length == N && dp(row - 1)(col - 1) != 0) return dp(row - 1)(col - 1)

  // 如果队列已经填满，则退出
