    void draw(); // draw snake and food on the screen
    void checkCollision(); // check for collision with wall (dx,dy) and snake head (dx,dy)
    void checkFood(); // check for collision with food (dx,dy) and snake head (dx,dy)
    void resetFood(); // reset food position in game field 
    void resetGame(); // reset game field - clean up snake head, food and walls from previous level 
private:
    int length; // length of snake body in cells
    int head; // position of snake head in cells (0..length-1) 
    int food; // position of food in cells (0..length-1) 
    int wall[4]; // four sides of the field - walls (0..length-1) 
}; 
#endif // SNAKE_H 
