def find_lcs(s1, s2):
    # Initialize a list to store the longest common subsequence.
    lcs = [[]] * len(s1) + [[]] * len(s2)

    # Initialize the length of the longest common subsequence to 0.
    lcs_length = 0

    # Loop through each character of both strings and find the longest common subsequence.
    for i in range(len(s1)):
        for j in range(len(s2)):
            if s1[i] == s2[j]:
                # If the characters match, add them to the LCS list and update the LCS length.
                lcs[i].append(s1[i])
                lcs[j].append(s2[j])
                lcs_length = max(lcs_length, i + j - 1)
            else:
                # Otherwise, remove the matching character from both LCS lists and update the LCS length.
                for k in range(len(lcs)):
                    if i in lcs[k]:
                        del lcs[k][i]
                    if j in lcs[k]:
                        del lcs[k][j]
                lcs_length = max(lcs_length, i + j - 1)

    return lcs_length

