#include <iostream>
#include <regex>

std::string markdown_to_html(const std::string& text) {
    std::regex markdown_regex("\\[([^]]*)\\]\\((.*?)\\)");
    std::smatch match;
    std::string result;
    for (std::string::const_iterator it = text.begin(); it != text.end(); ++it) {
        if (std::regex_match(it, match, markdown_regex)) {
            result += "<a href=\"";
            result += match[1];
            result += "\">";
            result += match[2];
            result += "</a>";
        } else {
            result += *it;
        }
    }
    return result;
}
``` 
