void timsort(int[] a) {
  int n = a.length;
  int l = 0, r = n - 1;
  while (l < r) {
    while (l < r && a[l] <= a[l + 1]) l++;
    while (l < r && a[r] >= a[r - 1]) r--;
    if (l < r) swap(a, l, r);
  }
}
