```
#!/usr/bin/python
from collections import deque

class OpaqueList(deque):
    """A mutable stack of objects that are not iterable."""
    def __repr__(self):
        return "<OpaqueList object at %d>" % id(self)
    def __len__(self):
        return len(super().__iter__())
    def __iter__(self):
        for item in super().__iter__():
            yield item.__repr__()
