public static boolean isPowerOfThree(int n) {
    if (n < 0 || n > Integer.MAX_VALUE) {
        return false;
    }

    int x = 0;
    while (n % 3 == 0) {
        x++;
        n /= 3;
    }

    return x == 1 && n == Integer.valueOf(3).pow(x);
}

