```C++
// Program to read a text file and count the number of appearances of a specific word
#include <iostream>
#include <fstream>
#include <string>
#include <vector>

using namespace std;

// Function to read a text file line by line and store the line in a vector
void readFile(ifstream& f, vector<string>& v) {
    string s;
    while (getline(f, s)) {
        v.push_back(s); // Add the line to the vector v of lines read from the file
    }
}

// Function to count the number of appearances of a specific word in a vector of lines read from the file
int findWord(vector<string>& v, string word) { // word: the word to find in the lines of the text file.
    int count = 0; // number of appearances of the word in the text file.
    for (int i = 0; i < v.size(); i++) { // Loop through all lines in the text file.
        if (v[i].find(word) != string::npos) // If the current line contains the word we are looking for...
            count++; // Increase the counter that counts how many times the word appears.
    }
    return count; // Return the number of appearances of the word.
}
```
